var parse= $jam$.$Parser( new function(){
    this[ /\d/.source ]=
    function( ){
        return '#'
    }
})
_test.ok( parse( 'a12bc' ).join(''), 'a##bc' )

var parse= $jam$.$Parser( new function(){
    this[ /([a-zA-Z])/.source ]=
    function( letter ){
        return letter
    }
    this[ '' ]= $jam$.$Value( '' )
})
_test.ok( parse( 'a12bc' ).join(''), 'abc' )

var parse= $jam$.$Parser( new function(){
    this[ /(_(\w+))/.source ]=
    function( underword, word ){
        return word
    }
    this[ /(\w)(\w*)/.source ]=
    function( first, tail ){
        return first.toUpperCase() + tail
    }
})
_test.ok( parse( '[_ab,c_d]' ).join(''), '[ab,C_d]' )
