var parse= $jam.$Parser( new function(){
    this.digit= new function(){
        this.regexp= /\d/
        this.handler= function( ){
            return '#'
        }
    }
})
_done( parse( 'a12bc' ).join(''), 'a##bc' )

var parse= $jam.$Parser( new function(){
    this.letter= new function(){
        this.regexp= /([a-zA-Z])/
        this.handler= function( letter ){
            return letter
        }
    }
    this['']= new function(){
        this.handler= $jam.$Value()
    }
})
_done( parse( 'a12bc' ).join(''), 'abc' )

var parse= $jam.$Parser( new function(){
    this.underword= new function(){
        this.regexp= /(_(\w+))/
        this.handler= function( underword, word ){
            return word
        }
    }
    this.word= new function(){
        this.regexp= /(\w)(\w*)/
        this.handler= function( first, tail ){
            return first.toUpperCase() + tail
        }
    }
})
_done( parse( '[_ab,c_d]' ).join(''), '[ab,C_d]' )
